import React from "react";
import onClickOutside from "react-onclickoutside";
import dropdownIcon from "../../../../Image/challenge_arrow.svg";
import "./index.scss";

const DateDropDown = (props) => {
  DateDropDown.handleClickOutside = () => props.onClickDropDownOutside();
  return (
    <div
      onClick={props.onClickDropdown}
      className={`challenge_work__dropdown ${props.isClickedDropdown ? "" : "hidden"}`}
    >
      <p>
        {props.currentDropdownMenu.split("-")[0]+"년 "+props.currentDropdownMenu.split("-")[1]+"월"}
        <img src={dropdownIcon} alt="" />
      </p>
      <ul>
        <li onClick={() => props.onClickDropdownList("2022-01")}>2022년 01월</li>
        <li onClick={() => props.onClickDropdownList("2021-12")}>2021년 12월</li>
        <li onClick={() => props.onClickDropdownList("2021-11")}>2021년 11월</li>
        <li onClick={() => props.onClickDropdownList("2021-10")}>2021년 10월</li>
        <li onClick={() => props.onClickDropdownList("2021-9")}>2021년 9월</li>
      </ul>
    </div>
  )
};

const clickOutsideConfig = {
  handleClickOutside: () => DateDropDown.handleClickOutside
};

export default onClickOutside(DateDropDown, clickOutsideConfig);
