import React from "react";
import Layout from "../../Common/Component/Layout";
import eventTitle from "../../Image/gather-event/gather-event-title.png";
import eventfindAllBrick from "../../Image/gather-event/gather-find-all-brick.png";
import eventfindAllBrickInfo from "../../Image/gather-event/gather-find-all-brick-info.png";
import eventfindInfo from "../../Image/gather-event/gather-find-info.png";
import eventfindTitle01 from "../../Image/gather-event/gather-find-title01.png";
import eventfindTitle02 from "../../Image/gather-event/gather-find-title02.png";
import "./index.scss";

const getImgSrc = (brick, option) => {
  return require(`../../Image/redbrick_block/block-${brick}-${option}.png`);
}

export default function View(props) {
  return (
    <Layout>
      <div className="gather-event">
        <div className="gather-event__wrap">
          <img 
            className="gather-event__title"
            src={eventTitle} 
            alt="REDBRICK 찾기"
          />
          <div className="gather-event__brick-wrap">
            <div>
            {props.firstLineBricks.map((brickInfo, i) => (
              <img 
                key={i}
                className={`gather-event__brick ${brickInfo.get ? "active" : ""}`}
                src={getImgSrc(brickInfo.name, brickInfo.get ? "active" : "off")} 
                alt="찾은 브릭 정보"
              />
            ))}
            </div>
            <div>
            {props.secondLineBricks.map((brickInfo, i) => (
              <img 
                key={i}
                className={`gather-event__brick ${brickInfo.get ? "active" : ""}`}
                src={getImgSrc(brickInfo.name, brickInfo.get ? "active" : "off")} 
                alt="찾은 브릭 정보"
              />    
            ))}
            </div>
          </div>
          <img 
            className="gather-event__find-brick"
            src={getImgSrc(props.findBrickName, "on")} 
            alt="찾은 브릭 정보"
          />
          <img 
            className="gather-event__find-title01"
            src={eventfindTitle01} 
            alt="브릭을"
          />
          <img 
            className="gather-event__find-title02"
            src={eventfindTitle02} 
            alt="찾으셨습니다."
          />
          { props.isFinished ?
            <>
            <img 
              className="gather-event__find-all-brick"
              src={eventfindAllBrick} 
              alt="모든 브릭을 찾았습니다."
            />
            <img 
              className="gather-event__find-all-brick-info"
              src={eventfindAllBrickInfo} 
              alt="회원정보에 이메일과 휴대전화를 확인해주세요. 회원정보가 정확하지 않은 경우, 당첨이 취소될 수 있습니다."
            />
            </> :
            <img 
              className="gather-event__find-info"
              src={eventfindInfo} 
              alt="다른 브릭도 찾아주세요."
            />
          }
        </div>
      </div>
    </Layout>
  )
}
