import React, { Component } from "react";
import { injectIntl } from "react-intl";
import "./index.scss";

class SearchInput extends Component {
  constructor(props) {
    super(props);
    this.inputRef = null;
    this.setInputRef = elem => {
      if (!elem) return;
      this.inputRef = elem;
    };
  }

  componentDidMount() {
    this.inputRef.addEventListener("keyup", e => {
      if (e.keyCode === 13) {
        this.props.onSubmit(e);
      }
    });
  }

  render() {
    const { value, onChange, onSubmit, tags } = this.props;
    return (
      <div className="GameSearchInput">
        <div className="Area">
          <input
            ref={this.setInputRef}
            className="GameSearchInput__input"
            type="text"
            value={value}
            placeholder={this.props.intl.formatMessage({
              id: "ID_SEARCH_HINT"
            })}
            onChange={onChange}
          />
          <p className="GameSearchInput__btn" onClick={onSubmit} />
        </div>
        {/* <hr className="DividerLine" /> */}
        {tags && (
          <div className="SearchTags">
            {tags.map((item, idx) => {
              return (
                <div className="TagItem" key={idx}>
                  <label key={idx}>#{item}</label>
                </div>
              );
            })}
          </div>
        )}
      </div>
    );
  }
}
export default injectIntl(SearchInput);
