import React from 'react';
import { FormattedMessage } from "react-intl";
import "./index.scss";

function AccountUpdatePopup(props) {
  const { history } = props;
  return (
    <div className="account-update">
      <p className="account-update__title">
        <FormattedMessage id={"ID_UPDATE_ACCOUNT_TITLE"} />
      </p>
      <p className="account-update__desc">
        <FormattedMessage id={"ID_UPDATE_ACCOUNT_DESC"} />
      </p>
      <div className="account-update__details">
        <p>
          <FormattedMessage id={"ID_UPDATE_ACCOUNT_DETAIL_TITLE"} />
        </p>
        <p>
          <FormattedMessage id={"ID_UPDATE_ACCOUNT_DETAIL_1"} />
        </p>
        <p>
          <FormattedMessage id={"ID_UPDATE_ACCOUNT_DETAIL_2"} />
        </p>
        <p>
          <FormattedMessage id={"ID_UPDATE_ACCOUNT_DETAIL_3"} />
        </p>
      </div>
      <p className="account-update__info">
        <FormattedMessage id={"ID_UPDATE_ACCOUNT_INFO"} />
      </p>
      <button
        onClick={() => {
          history.push("/myaccount");
          props.dismiss();
        }}
      >
        <FormattedMessage id={"ID_UPDATE_ACCOUNT_BUTTON"} />
      </button>
    </div>
  )
}

export default AccountUpdatePopup;
