export const filterBlindedComments = comments => {
  return comments
    .filter(comment => !comment.isBlind)
    .map(comment => {
      if (comment.replies.length > 0) {
        const replies = comment.replies.filter(reply => !reply.isBlind);
        return { ...comment, replies };
      }
    return comment;
  })
}

export const filterBlindedCommentsFromArticle = comments => {
  return comments
    .filter(comment => !comment.isBlind)
    .map(comment => {
      if (comment.childComments.length > 0) {
        const childComments = comment.childComments.filter(comment => !comment.isBlind);
        return { ...comment, childComments };
      }
      return comment;
    })
}

export const getBlindedCommentCount = comments => {
  return comments.filter(({ isBlind }) => isBlind).length;
}
