import React, { Component } from "react";
import "./index.scss";
import { injectIntl } from "react-intl";
import * as request from "../../../../Common/Util/HTTPRequest";
import moment from "moment";
import Field from "../../../../Common/Component/Field";

class AccountBirthPopup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      birth: {
        year: undefined,
        month: undefined,
        date: undefined
      }
    }
  }

  onChangeField = (id, value) => {
    this.setState({ [id]: value })
  }

  onClickSubmit = () => {
    const { birth } = this.state;
    if (!birth.year || birth.month == null || !birth.date) return;

    const params = {
      birth : moment([birth.year, birth.month, birth.date]).format("YYYY-MM-DD")
    }
    request
      .updateUserInfo(params)
      .then(response => response.json())
      .then(json => {
        if(json.success){
          this.props.dismiss()
          if(this.props.onCompleted) this.props.onCompleted(params.birth)
        }else{

        }
      })
  }

  render() {
    const { intl } = this.props
    const { birth } = this.state

    return (
      <div className="account__manage__birth-popup">
        <div className="account__manage__birth-popup__title">
          {intl.formatMessage({ id: "ID_MY_ACCOUNT_BIRTH_POPUP_TITLE"})}
        </div>
        <div className="account__manage__birth-popup__msg">
          {intl.formatMessage({ id: "ID_MY_ACCOUNT_BIRTH_POPUP_MSG"})}
        </div>

        <Field.Date
          id="birth"
          value={birth}
          onChange={this.onChangeField}
        />

        <button className="account__manage__birth-popup__confirm-btn"  
          disabled={false} onClick={this.onClickSubmit}>
          {intl.formatMessage({ id: "ID_WIZLIVE_CLASS_MESSAGE_SUCCESS_OK"})}
        </button>
      </div>
    );
  }
}


export default injectIntl(AccountBirthPopup);
