import React, { Component } from "react";
import { injectIntl } from "react-intl";
import moment from "moment";
import "./index.scss";

import eyeOn from "../../../Image/signup/eye-on.svg";
import eyeOff from "../../../Image/signup/eye-off.svg";

function Text(props) {
  return (
    <Field {...props} >
      <input
        { ...props }
        className={"field__content__input"}
        type="text"
        onChange={e => {
          if (props.onChange) props.onChange(props.id, e.currentTarget.value);
        }}
        onBlur={e => {
          if (props.onBlur) props.onBlur(props.id, e.currentTarget.value);
        }}
      />
    </Field>
  );
}
class SecureText extends Component {
  constructor(props) {
    super(props);
    this.state = {
      secure: true
    };
    this.inputRef = React.createRef();
    this.focus = () => {
      this.inputRef.current.focus();
    }
  }
  render() {
    return (
      <Field {...this.props} >
        <div className="field__content--secure">
          <input
            { ...this.props }
            className={"field__content__input"}
            type={this.state.secure ? "password" : "text"}
            onChange={e => {
              if (this.props.onChange) this.props.onChange(this.props.id, e.currentTarget.value);
            }}
            onBlur={e => {
              if (this.props.onBlur) this.props.onBlur(this.props.id, e.currentTarget.value);
            }}
            ref={this.inputRef}
          />
          {this.props.secureToggleOption &&
            <img
              className="field__content__secure-toggle"
              src={this.state.secure ? eyeOff : eyeOn}
              alt="secure toggle"
              onClick={()=>{ this.setState({secure: !this.state.secure}) }}
            />
          }
        </div>
      </Field>
    );
  }
}

function Date(props) {
  const { value } = props;
  const year = value.year;
  const month = value.month;
  const date = value.date;

  const years = [...Array(60)].map((_, index) => moment().year() - index);
  const months = [...Array(12)].map((_, index) => index);
  const dates = month == null ? [] : [...Array(moment([year, month, 1]).endOf("month").date())].map(
    (_, index) => 1 + index
  );

  const getValidDate = (year, month, date) => {
    if (year !== undefined && month != null) {
      const lastDate = moment([year, month, 1]).endOf("month").date();
      return date > lastDate ? lastDate : date;
    }
    return date;
  }

  return (
    <Field {...props} >
      <div className="field__content__selects">
        <select
          className={`field__content__select--year empty--${!year}`}
          defaultValue=""
          value={year}
          onChange={e => {
            const year = Number(e.currentTarget.value);
            const _value = {
              ...value,
              year,
              date: getValidDate(year, value.month, value.date)
            }
            if (props.onChange) props.onChange(props.id, _value);
          }}
        >
          <option disabled hidden value="">
            {props.intl.formatMessage({ id: "ID_SIGNUP_YEAR" })}
          </option>
          {years.map(y => <option key={y} value={y}>{y}</option>)}
        </select>
        <select
          className={`field__content__select--month empty--${month==null}`}
          defaultValue=""
          value={month}
          onChange={e => {
            const month = Number(e.currentTarget.value);
            const _value = {
              ...value,
              month,
              date: getValidDate(value.year, month, value.date)
            }
            if (props.onChange) props.onChange(props.id, _value);
          }}
          disabled={!year}
        >
          <option disabled hidden value="">
            {props.intl.formatMessage({ id: "ID_SIGNUP_MONTH" })}
          </option>
          {months.map(m => <option key={m} value={m}>{m + 1}</option>)}
        </select>
        <select
          className={`field__content__select--date empty--${!date}`}
          defaultValue=""
          value={date}
          onChange={e => {
            const date = parseInt(e.currentTarget.value);
            const _value = {
              ...value,
              date
            }
            if (props.onChange) props.onChange(props.id, _value);
          }}
          disabled={!year || month==null}
        >
          <option disabled hidden value="">
            {props.intl.formatMessage({ id: "ID_SIGNUP_DATE" })}
          </option>
          {dates.map(d => <option key={d} value={d}>{d}</option>)}
        </select>
      </div>
    </Field>
  );
}

function Field(props) {
  const {
    children,
    title,
    warning,
    okSign,
    informations = []
  } = props;
  return (
    <div className="field">
      {title && <p className="field__title">{title}</p>}
      <div className={`field__content invalid--${!!warning}`}>
        {children}
        <p className="field__content__warning">
          {warning}
        </p>
        <p className="field__content__ok-sign">
          {okSign}
        </p>
        <div className="field__content__infos">
          {
            informations.map((info, i) => (
              <p key={i} className={`field__content__info valid--${info.valid}`}>
                {info.text}
              </p>
            ))
          }
        </div>
      </div>
    </div>
  );
}

export default {
  Text,
  SecureText,
  Date: injectIntl(Date)
};
