import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import View from "./View";
import * as userInfoActions from "../../Common/Store/Reducer/UserInfo";

class Container extends Component {
  constructor(props) {
    super(props);

    try {
      if (props.history.location.pathname === "/codingparty") {
        props.history.replace("/");
      }
    } catch (e) {}
  }

  updateUserIconUrl = (imageUrl) => {
    const params = {
      email: this.props.userinfo.email,
      icon: imageUrl
    };
    this.props.updateUserInfo(params);
  }

  render() {
    return <View  
      updateIconCallbak={this.updateUserIconUrl}
    />;
  }
}

export default connect(
  (state) => ({
    userId: state.userinfo.id,
    userinfo: state.userinfo
  }),
  {
    updateUserInfo: userInfoActions.updateUserInfo
  }
)(withRouter(Container));
