import React from "react";
import { FormattedMessage } from "react-intl";
import { Link } from "react-router-dom";
import Layout from "../../Common/Component/Layout";
import LearnDetail from "./Component/LearnDetail";
import Categories from "./Component/Categories";
import learnJs from "../../Image/learn-js.png";
import learnPython from "../../Image/learn-python.png";
import "./index.scss";

export default function View(props) {
  const {
    categories,
    selectedCategoryIndex,
    selectedProjectId,
    onlineLearnings,
    onClickCategory,
    onClickProject,
    onClickOnlineLearningTab,
    selectedOnlineLearningTab
  } = props;

  return (
    <Layout className={selectedProjectId ? "layout--learn layout--learn--project" : "layout--learn"}>
      <div className="page__learn">
        <div className="page__learn__main">
          <div className="page__learn__main-img"/>
        </div>
        <OnlineLearning
          onlineLearnings={onlineLearnings}
          onClickProject={onClickProject}
          onClickOnlineLearningTab={onClickOnlineLearningTab}
          selectedOnlineLearningTab={selectedOnlineLearningTab}
        />
        <Categories
          categories={categories}
          selectedCategoryIndex={selectedCategoryIndex}
          onClickCategoryAt={onClickCategory}
        />
        <CodingParty
          categories={categories}
          selectedCategoryIndex={selectedCategoryIndex}
          onClickCategory={onClickCategory}
          onClickProject={onClickProject}
        />
        <LiveLearning />
        {selectedProjectId && <LearnDetail projectId={selectedProjectId} onClose={onClickProject} />}
      </div>
    </Layout>
  );
}

const CodingParty = props => {
  const { 
    categories,
    selectedCategoryIndex,
    onClickCategory,
    onClickProject
  } = props;
  if(categories.length === 0) {
    return(
      <div className="coding_party" />
    );
  }

  const {
    subtitle,
    title,
    introduction,
    triedCount,
    completedCount,
    projects
  } = categories[selectedCategoryIndex];
  return(
    <div className="coding_party">
      <div className="coding_party__category">
        <div className="coding_party__category__subtitle">{subtitle}</div>
        <div className="coding_party__category__title">{title}</div>
        <div className="coding_party__category__introduction">{introduction}</div>
        <div className="coding_party__category__counts">
          <div className="coding_party__category__count">
            <FormattedMessage id="ID_LEARN_CODINGPARY_TRIED_COUNT" values={{count: triedCount}} />
          </div>
          <div className="coding_party__category__count">
            <FormattedMessage id="ID_LEARN_CODINGPARY_COMPLETED_COUNT" values={{count: completedCount}} />
          </div>
        </div>
      </div>
      <div className="coding_party__projects">
        {projects.map((project, index)=>{
          return (
            <div className="coding_party__project" key={index} onClick={()=>onClickProject(project.id)}>
              <img className="coding_party__project__image" src={project.image} alt="project img" />
              <div className="coding_party__project__grade">{project.grade}</div>
              <div className="coding_party__project__title">{project.title}</div>
              <div className="coding_party__project__participants">
                <FormattedMessage id="ID_LEARN_CODINGPARY_PARTICIPANT_COUNT" values={{count: project.triedCount}} />
              </div>
            </div>
          );
        })}
      </div>
      <Link to="/challenge">
        <div className="coding_party__banner" />
      </Link>
    </div>
  );
}

const OnlineLearning = props => {
  const {
    onlineLearnings,
    onClickProject,
    onClickOnlineLearningTab,
    selectedOnlineLearningTab
  } = props;
  return (
    <div className="online_learning">
      <div className="online_learning__wrapper">
        <div className="online_learning__title"><FormattedMessage id="ID_LEARN_CODINGPARY_ONLINE_TITLE" /></div>
        <div className="online_learning__subtitle"><FormattedMessage id="ID_LEARN_CODINGPARY_ONLINE_SUBTITLE" /></div>
        {/* <div className="online_learning__info">
          <img src={alertIcon} alt="" />
          <FormattedMessage id="ID_LEARN_CODINGPARY_ONLINE_INFO" />
        </div> */}
        <div className="online_learning__tabs">
          <div
            className={`online_learning__tab ${selectedOnlineLearningTab==="all" && "tab--active"}`}
            onClick={()=>{onClickOnlineLearningTab("all")}}
          >
            <FormattedMessage id="ID_LEARN_CODINGPARY_ONLINE_ALL" />
          </div>
          <div
            className={`online_learning__tab ${selectedOnlineLearningTab==="oobc" && "tab--active"}`}
            onClick={()=>{onClickOnlineLearningTab("oobc")}}
          >
            <FormattedMessage id="ID_LEARN_CODINGPARY_ONLINE_BLOCK" />
          </div>
          <div
            className={`online_learning__tab ${selectedOnlineLearningTab==="js" && "tab--active"}`}
            onClick={()=>{onClickOnlineLearningTab("js")}}
          >
            <FormattedMessage id="ID_LEARN_CODINGPARY_ONLINE_TEXT" />
          </div>
        </div>
        <div className="online_learning__items">
          {onlineLearnings.map((onlineLearing,index)=>{
            return (
              <div onClick={()=>onClickProject(onlineLearing.id)} className="online_learning__item" key={index}>
                <img className="online_learning__item__image" src={onlineLearing.smallImage} alt="online-learning" />
                <div className="online_learning__item__wrapper">
                  <div className="online_learning__item__grade">{onlineLearing.grade}</div>
                  <div className="online_learning__item__info">{onlineLearing.lang.toUpperCase()}</div>
                </div>
                <div className="online_learning__item__title">{onlineLearing.title}</div>
              </div>
            );
          })}
        </div>
        {/* <div className="online_learning__view-more-button"><img src={viewMoreIcon} alt="view more"/>
          <FormattedMessage id="ID_LEARN_CODINGPARY_VIEW_MORE" />
        </div> */}
      </div>
    </div>
  );
}

const LiveLearning = props => {
  return (
    <div className="live_learning">
      <div className="live_learning--wrap">
        <div className="live_learning__subtitle"><FormattedMessage id="ID_LEARN_CODINGPARY_LIVE_SUBTITLE" /></div>
        <div className="live_learning__desc"><FormattedMessage id="ID_LEARN_CODINGPARY_LIVE_DESC1" /></div>
        <a className="live_learning__freetrial-button" href="https://wizlive.com/?trial=1" target="_blank" rel="noopener noreferrer">
          <FormattedMessage id="ID_LEARN_CODINGPARY_LIVE_BTN" />
          </a>
        <div className="live_learning__items">
          <div className="live_learning__item">
            <img className="live_learning__item__image" src={learnJs} alt="live-learning" />
            <div className="live_learning__item__title"><FormattedMessage id="ID_LEARN_CODINGPARY_LIVE_ITEM_PYTHON_TITLE" /></div>
            <div className="live_learning__item__desc"><FormattedMessage id="ID_LEARN_CODINGPARY_LIVE_ITEM_PYTHON_DESC" /></div>
          </div>
          <div className="live_learning__item">
            <img className="live_learning__item__image" src={learnPython} alt="live-learning" />
            <div className="live_learning__item__title"><FormattedMessage id="ID_LEARN_CODINGPARY_LIVE_ITEM_JS_TITLE" /></div>
            <div className="live_learning__item__desc"><FormattedMessage id="ID_LEARN_CODINGPARY_LIVE_ITEM_JS_DESC" /></div>
          </div>
        </div>
      </div>
    </div>
  );
}
