import React, { Fragment } from "react";
import { Link } from "react-router-dom";
import WizTooltip from "../../../../Common/Component/Tooltip";
import UserIconWrapper from "../../../../Common/Component/UserIconWrapper";
import "./index.scss";

import settingIcon from "../../../../Image/icon-setting.svg";
import tooltipIcon from "../../../../Image/icon-tooltip.svg";
import eventIcon from "../../../../Image/icon-event-next.svg";
import officialIcon from "../../../../Image/certification-mark.svg";
const UserInfoTemplate = props => {
  const {
    userInfo,
    onClickSettingBtn,
    match,
    intl,
    onClickMobileTooltip
  } = props;
  const isMyPage = match.path.includes("mypage");

  const tierTooltip = intl.formatMessage({
    id: `ID_MYPAGE_TOOLTIP_TIER`
  });
  return (
    <section className="UserInfoTemplate">
      <div className="UserInfoTemplate_UserInfo">
        <div className="UserInfoTemplate_UserInfo_ImgWrap">
          <div className="UserInfoTemplate_UserInfo_ImgWrap-icon">
            <UserIconWrapper iconSrc={userInfo.icon} />
          </div>
          {isMyPage ? (
            <Link
              to="/iconsetting"
              className="UserInfoTemplate_UserInfo_ImgWrap-setting"
            >
              <img
                className="UserInfoTemplate_UserInfo_TextWrap_FirstLine-settingBtn"
                src={settingIcon}
                alt="img"
              />
            </Link>
          ) : null}
        </div>
        <div className="UserInfoTemplate_UserInfo_TextWrap">
          <div className="UserInfoTemplate_UserInfo_TextWrap_FirstLine">
            <div className="UserInfoTemplate_UserInfo_TextWrap_FirstLine-nik">
              {userInfo.name}
            </div>
            <div className="UserInfoTemplate_UserInfo_TextWrap_FirstLine-tier">
              {userInfo.isOfficial && (
                <div className="UserInfoTemplate_UserInfo_TextWrap_FirstLine-official">
                  <img
                    className="UserInfoTemplate_UserInfo_TextWrap_FirstLine-official_img"
                    src={officialIcon}
                    alt={userInfo.name}
                  />
                </div>
              )}
              {isMyPage && (
                <Fragment>
                  <div className="UserInfoTemplate-tooltip">
                    <WizTooltip
                      contentTitle={tierTooltip}
                      container={
                        <img
                          src={tooltipIcon}
                          alt="img"
                          style={{ width: "12px", height: "12px" }}
                        />
                      }
                    />
                  </div>
                  <img
                    className="UserInfoTemplate-tooltipMobile"
                    src={tooltipIcon}
                    alt="img"
                    onClick={() => {
                      onClickMobileTooltip(tierTooltip);
                    }}
                  />
                </Fragment>
              )}
              {onClickSettingBtn && (
                <div
                  className="UserInfoTemplate_UserInfo_setting"
                  onClick={() => {
                    onClickSettingBtn({ type: "name" });
                  }}
                >
                  <img
                    className="UserInfoTemplate_UserInfo_TextWrap_FirstLine-settingBtn"
                    src={settingIcon}
                    alt="img"
                  />
                </div>
              )}
            </div>
          </div>
          <div className="UserInfoTemplate_UserInfo_etc">
            <div className="UserInfoTemplate_UserInfo_TextWrap-status">
              {userInfo.statusMessage
                ? userInfo.statusMessage
                : intl.formatMessage({ id: "ID_MYPAGE_USERINFO_STATUSMAG" })}
            </div>
            <div className="UserInfoTemplate_UserInfo_TextWrap-organization">
              {userInfo.organization
                ? userInfo.organization
                : intl.formatMessage({
                    id: "ID_MYPAGE_USERINFO_ORGANIZATION"
                  })}
            </div>
          </div>
        </div>
      </div>

      <div className="UserInfoTemplate_Wrap">
        {userInfo && onClickSettingBtn && (
          <ItemWrap
            intl={intl}
            match={match}
            mode="point"
            levelPoint={userInfo.levelPoint}
            onClickMobileTooltip={onClickMobileTooltip}
          />
        )}
        {userInfo && (
          <ItemWrap
            intl={intl}
            match={match}
            mode="subscribe"
            subscribeCount={userInfo.subscribeCount}
          />
        )}
        {userInfo && (
          <ItemWrap
            intl={intl}
            match={match}
            mode="interested"
            interestedCount={userInfo.interestedCount}
          />
        )}
      </div>
    </section>
  );
};

export default UserInfoTemplate;

const ItemWrap = props => {
  const {
    intl,
    mode,
    match,
    levelPoint,
    subscribeCount,
    interestedCount,
    onClickMobileTooltip
  } = props;
  const tooltipText = intl.formatMessage({ id: "ID_MYPAGE_TOOLTIP_POINT" });
  const isUserPage = match.path === "/userpage/:id";
  // let title;
  let num;
  switch (mode) {
    case "point":
      // title = "위즈포인트";
      num = levelPoint.toLocaleString();
      break;

    case "subscribe":
      // title = "구독자";
      num = subscribeCount.toLocaleString() + "명";
      break;

    case "interested":
      // title = "관심회원";
      num = interestedCount.toLocaleString() + "명";
      break;

    default:
      break;
  }

  return (
    <div className="UserInfoTemplate_ItemWrap">
      <div
        className="UserInfoTemplate_ItemWrap_Inner"
        style={mode === "point" ? { marginLeft: "39px" } : null}
      >
        <div className="UserInfoTemplate_ItemWrap_TitleWrap">
          <div className="UserInfoTemplate_ItemWrap_TitleWrap-title">
            {intl.formatMessage({
              id: `ID_MYPAGE_USERINFO_${mode.toUpperCase()}`
            })}
          </div>
          {mode === "point" && (
            <Fragment>
              <div className="UserInfoTemplate-tooltip">
                <WizTooltip
                  contentTitle={tooltipText}
                  container={
                    <img
                      src={tooltipIcon}
                      alt="img"
                      style={{ width: "12px", height: "12px" }}
                    />
                  }
                />
              </div>
              <img
                className="UserInfoTemplate-tooltipMobile"
                src={tooltipIcon}
                alt="img"
                onClick={() => {
                  onClickMobileTooltip(tooltipText);
                }}
              />
            </Fragment>
          )}
        </div>
        <Link
          to={
            isUserPage
              ? `/userpage/${match.params.id}/${mode}`
              : mode === "point"
              ? mode
              : `/mypage/${mode}`
          }
          style={
            mode === "point"
              ? { textDecoration: "none", pointerEvents: "none" }
              : { textDecoration: "none" }
          }
          className="UserInfoTemplate_ItemWrap_BottomWrap"
        >
          <div className="UserInfoTemplate_ItemWrap_BottomWrap-num">{num}</div>
          {mode !== "point" && <img src={eventIcon} alt="img" />}
        </Link>
      </div>
    </div>
  );
};
