import React from "react";
import Layout from "../../../Common/Component/Layout";
import "./index.scss";
import Img1 from "../../../Image/banner-event2.png";
import Img2 from "../../../Image/event2_detail_mobile.png";
import liveBannerImg from "../../../Image/wizlive_banner02.png";

export default function View(props) {
  const {
    codeInfo,
    userinfo,
    recommendCodes,
    // onClickShare,
    // onClickLogin,
    onClickEvent1Banner
  } = props;

  const getSecretEmail = email => {
    const splitted = email.split("@");
    if (!splitted || splitted.length !== 2) {
      return;
    }
    const left = splitted[0];
    const center = "*****";
    const right = "***";

    if (!left) {
      return;
    }

    const newLeft = Array.from(left)
      .map((element, index) => {
        if (left.length < 2) {
          return element;
        }
        if (index === 0 || index === 1) {
          return element;
        } else {
          return "*";
        }
      })
      .join("");

    return newLeft + "@" + center + "." + right;
  };

  return (
    <Layout>
      <div className="Page--EventDetail">
        <div className="Event_Inner">
          <img className="Event2-img1-pc" src={Img1} alt="img" />
          <img className="Event2-img1-mobile" src={Img2} alt="img" />
          {userinfo.email ? (
            <div className="Event2-code">이벤트가 종료되었습니다.</div>
          ) : (
            <div
              className="Event2-code Event-code-login"
              // style={{ cursor: "pointer" }}
              // onClick={onClickLogin}
            >
              이벤트가 종료되었습니다.
            </div>
          )}
          {/* <div className="Event2_BtnWrap">
            <div
              className="Event2-shareBtn"
              id="Event2-shareBtn-kakao"
              onClick={() => {
                onClickShare("kakao");
              }}
            />
            <div
              className="Event2-shareBtn"
              id="Event2-shareBtn-facebook"
              onClick={() => onClickShare("facebook")}
            />
          </div> */}

          {userinfo.email ? (
            <div className="Event2-msg1">
              내가 초대한 친구는 <p>{codeInfo && codeInfo.signedCount}</p>명
              입니다
            </div>
          ) : (
            <div className="Event2-msg1">
              친구를 초대하고 선물을 받아보세요!
            </div>
          )}

          <div className="Event2_TableWrap">
            {recommendCodes.map((element, index) => {
              return (
                <RankingItem
                  key={index}
                  index={index}
                  nik={element.user.name}
                  icon={element.user.icon}
                  id={getSecretEmail(element.user.email)}
                  signedCount={element.signedCount}
                  isIn={element.user.email === userinfo.email ? true : false}
                />
              );
            })}
          </div>

          <div className="Event2-event1" onClick={onClickEvent1Banner} />
          {/* <img
            className="Event2-img1-mobile Event2-img1-mobile-bottom"
            src={Img2_2}
            alt="img"
          /> */}
        </div>
        <a
          href="https://wizlive.com/?trial=3"
          target="_blank"
          rel="noopener noreferrer"
        >
          <img class="Event2_LiveBanner" src={liveBannerImg} alt="live" />
        </a>
      </div>
    </Layout>
  );
}

const RankingItem = props => {
  const { index, nik, id, signedCount, isIn, icon } = props;

  return (
    <div
      className="RankingItem"
      style={isIn ? { backgroundColor: "#EBE1FF" } : null}
    >
      <div className="RankingItem-index">{index + 1}</div>
      <div className="RankingItem_IconWrap">
        <div className="RankingItem_IconWrap_ImgWrap">
          <img className="RankingItem-icon" src={icon} alt="img" />
        </div>
      </div>
      <div className="RankingItem-nik">{nik}</div>
      <div className="RankingItem-id">{id}</div>
      <div className="RankingItem-count">{signedCount}명</div>
    </div>
  );
};
