import React from "react";
import { Link } from "react-router-dom";
import { FormattedMessage } from "react-intl";
import Layout from "../../Common/Component/Layout";
import EditorPicks from "../../Common/Component/EditorPicks";
import EventSlides from "./Components/EventSlides";
import WeeklyBests from "./Components/WeeklyBests";
import CreatorRanks from "./Components/CreatorRanks";
// import Templates from "./Components/Templates";
import "./index.scss";

function View(props) {
  return (
    <Layout>
      <div className="page page--home">
        <HeroSection />
        <div className="page__centered">
          <EditorPickSection />
          <WeeklyBestSection />
          <CreatorRankSection />
        </div>
      </div>
    </Layout>
  )
}

function HeroSection(props) {
  return (
    <div className="page__section page__section--hero">
      <EventSlides />
    </div>
  )
}


function EditorPickSection(props) {
  return (
    <div className="page__section page__section--editor-picks">
      <div className="page__section__header">
        <div className="page__section__title">
          <FormattedMessage id="ID_HOME_EDITOR_TITLE" />
        </div>
        <Link
          className="page__section__caption"
          to="/editorRecommend"
        >
          <FormattedMessage id="ID_HOME_EDITOR_MORE" />
        </Link>
      </div>
      <div className="page__section__body">
        <EditorPicks />
      </div>
    </div>
  )
}
function WeeklyBestSection(props) {
  return (
    <div className="page__section page__section--weekly-bests">
      <div className="page__section__header">
        <div className="page__section__title">
          <FormattedMessage id="ID_HOME_WEEKLY_BEST_TITLE" />
        </div>
      </div>
      <div className="page__section__body">
        <WeeklyBests />
      </div>
    </div>
  )
}
function CreatorRankSection(props) {
  return (
    <div className="page__section page__section--creator-ranks">
      <div className="page__section__header">
        <div className="page__section__title">
          <FormattedMessage id="ID_HOME_RANK_CREATOR_TITLE" />
        </div>
      </div>
      <div className="page__section__body">
        <CreatorRanks />
      </div>
    </div>
  )
}

export default View;
